import { useState,useEffect } from "react";
import Card from "../UI/Card";
import axios from "axios";
import "./Estatistica.css";
const Estatistica = () => {
   const [estadistica,setEstadistica]=useState({oldestDate:"",newestDate:""})
   const [text,setText]=useState('')
   const [urlPopular,setUrlPopular]=useState({url:"", cantidad:""})
   const [cantTweets,setCantTweets]=useState('')
   const [mensiones,setMensiones]=useState({mentions:"", username:""})
	const fecha = () => {
		axios.get("http://localhost:9876/api/v1/tweets/dates/tweets",{
				headers: {"Authorization": `Bearer ${localStorage.getItem("auth")}`}
			  })
			.then((response) => {
				setEstadistica({...estadistica,oldestDate:response.data.oldestDate,newestDate:response.data.newestDate});
			})
	};
	const masPopular=()=>{
		axios.get("http://localhost:9876/api/v1/tweets/retweet/tweets",{
				headers: {"Authorization": `Bearer ${localStorage.getItem("auth")}`}
			  })
			.then((response) => {
				setText(response.data.text);
	  })
	}

	const masPopularUrl=()=>{
		axios.get("http://localhost:9876/api/v1/tweets/top-urls/tweets",{
				headers: {"Authorization": `Bearer ${localStorage.getItem("auth")}`}
			  })
			.then((response) => {
				setUrlPopular({...urlPopular,url:response.data[0].url,cantidad:response.data[0].cantidad})
	  })
	}
	
	const cantidadTweets=()=>{
		axios.get("http://localhost:9876/api/v1/tweets/count/tweets",{
				headers: {"Authorization": `Bearer ${localStorage.getItem("auth")}`}
			  })
			.then((response) => {
				setCantTweets(response.data)
	  })
	}

	const mensionTweets=()=>{
		axios.get("http://localhost:9876/api/v1/tweets/top-mentions/tweets",{
				headers: {"Authorization": `Bearer ${localStorage.getItem("auth")}`}
			  })
			.then((response) => {
			console.log(response.data)
	  })
	}

	console.log(mensiones)

var date1 = new Date(estadistica.oldestDate);
var date2 = new Date(estadistica.newestDate);

useEffect(() => {
	fecha()
	masPopular()
	masPopularUrl()
	cantidadTweets()
	mensionTweets()
}, []);
	return (
		<div className="container">
			<br />
			<br />

				<Card>
					<table className="table">
						
						<tbody >
							<tr>
								
								<td>Fecha de descarga</td>
								<td>Desde {date1.getDate()}/{date1.getMonth()+1}/{date1.getFullYear()}/{date1.getHours()}h:{date1.getMinutes()}s.  Hasta {date2.getDate()}/{date2.getMonth()+1}/{date2.getFullYear()}/{date2.getHours()}h:{date2.getMinutes()}s.</td>
							</tr>
							<tr>
							
								<td>Tweet más retuiteado.</td>
								<td>{text}</td>
							</tr>
							<tr>
								
								<td>Url más popular</td>
								<td>{urlPopular.url} - <span>Numero de veces {urlPopular.cantidad}</span></td>
							</tr>
							<tr>
								
								<td>Cantidad de tweets</td>
								<td>Hay {cantTweets} tweets guardados en el sistema</td>
							</tr><tr>
								
								<td>Usuarios más mencionados</td>
								<td>Carlos Argentino - <span>Numero de veces 12</span></td>
							</tr>
						</tbody>
					</table>
				</Card>


{/*-------------------------------------------------------------------------usuarios con mas tweets publicados --------------------------------------- */}
				<Card>
					<table className="table">
						<thead>
							<tr>
								
								<th scope="col">Usuario </th>
								<th scope="col">Mensiones</th>
							</tr>
						</thead>
						<tbody>
							<tr>
								
								<td>Carlos</td>
								<td>8 tweets</td>
							</tr>
							<tr>
							
								<td>Enrique</td>
								<td>21 tweets</td>
							</tr>
							<tr>
								
								<td>Juliao</td>
								<td>12 tweets</td>
							</tr>
							<tr>
								
								<td>Daniel</td>
								<td>14 tweets</td>
							</tr><tr>
								
								<td>Pablo</td>
								<td>19 tweets</td>
							</tr>
						</tbody>
						
					</table>
				</Card>
			</div>
		
	);
};

export default Estatistica;
