import "./Login.css";
import Card from "../UI/Card";
import {useState} from "react";

const Login = ({estaLogueado}) => {

	const [datosUsuario, setUsuario] = useState({usuario: "", contraseña: ""});

	const inputChange = (e) => {
		setUsuario({
			...datosUsuario,
			[e.target.name]: e.target.value,
		});
	};

    const enviaDatos=(e)=>{
        e.preventDefault();
      estaLogueado(datosUsuario)
    }
  
	return (
		<section className="container-fluid">
			<section className="row justify-content-center">
				<div className="form-containerLibro col-12 col-md-8 col-lg-8">
					<br />
					<br />
					<br />
					<h4 className="mb-1 text-center">Autenticar Primero</h4>
					<Card>
						<br />
						<br />
						<br />
						<form onSubmit={enviaDatos} className="row g-3 mr-4 ml-4" encType="multipart/form-data">
							<div className="col-12">
								<label htmlfor="titulo" class="form-label">
									Usuario
								</label>
								<input
									type="text"
									id="tituloLibro"
									className="form-control"
									name="usuario"
									onChange={inputChange}
                                    datosUsuario={datosUsuario.usuario} 
									required
								/>
							</div>
							<div class="col-12">
								<label htmlfor="editor" class="form-label">
									Contraseña
								</label>
								<input
									type="text"
									className="form-control"
									name="contraseña"
									required
									onChange={inputChange}
                                    datosUsuario={datosUsuario.contraseña} 
								/>
							</div>

							<div className="col-12 pt-2">
								<button className="btn btn-primary ">Autenticar</button>
							</div>
						</form>
						<br />
						<br />
						<br />
					</Card>
				</div>
			</section>
		</section>
	);
};
export default Login;
