import React, {useState} from "react";
import {useRef} from "react";
import Wrapper from "../Helpers/Wrapper";
import Button from "../UI/Button";
import Card from "../UI/Card";
import ErrorModal from "../UI/ErrorModal";
import styles from "./AddUser.module.css";

const AddUser = (props) => {
	const nameInputRef = useRef("");
	const ageImputRef = useRef("");

	const [enteredUser, setEnteredUser] = useState({username: "", age: ""});
	const [error, setError] = useState();

	const userChangeHandler = (event) => {
		console.log(ageImputRef.current.value)
		setEnteredUser((prev) => {
			return {...prev, [event.target.name]: event.target.value};
		});
	};

	const addUserHandler = (event) => {
		event.preventDefault();
		if (
			enteredUser.username.trim().length === 0 ||
			enteredUser.age.trim().length === 0
		) {
			return setError({
				title: "Invalid input",
				message: "Please enter a valid name and age (non-empty values).",
			});
		}

		if (+enteredUser.age < 1) {
			return setError({
				title: "Invalid age",
				message: "Please enter a valid age (>0).",
			});
		}
		props.onAddUser(enteredUser);
		setEnteredUser({username: "", age: ""});
	};

	const errorHandler = () => {
		setError(null);
	};

	return (
		<Wrapper>
			{error && (
				<ErrorModal
					onCloseModal={errorHandler}
					title={error.title}
					message={error.message}
				/>
			)}
			<Card className={styles.input}>
				<form onSubmit={addUserHandler}>
					<label htmlFor="username">Username</label>
					<input
						onChange={userChangeHandler}
						value={enteredUser.username}
						name="username"
						id="username"
						type="text"
						ref={nameInputRef}
					/>
					<label htmlFor="age">Age (Years)</label>
					<input
						onChange={userChangeHandler}
						value={enteredUser.age}
						name="age"
						id="age"
						type="number"
						ref={ageImputRef}
					/>
					<Button type="submit">Add User</Button>
				</form>
			</Card>
		</Wrapper>
	);
};

export default AddUser;
