import React from "react";
import ReactDOM from "react-dom";
import Card from "./Card";
import Button from "./Button";
import styles from "./ErrorModal.module.css";
import Wrapper from "../Helpers/Wrapper";

const Backdrop = (props) => {
	return <div className={styles.backdrop} onClick={props.onCloseModal} />;
};

const ModalOverlay = (props) => {
	return (
		<Card className={styles.modal}>
			<header className={styles.header}>
				<h2>{props.title}</h2>
			</header>
			<div className={styles.content}>
				<p>{props.message}</p>
			</div>
			<footer className={styles.actions}>
				<Button onClick={props.onCloseModal}>Okay</Button>
			</footer>
		</Card>
	);
};

const ErrorModal = (props) => {
	return <Wrapper>
		{ReactDOM.createPortal(<Backdrop onCloseModal={props.onCloseModal}/>,document.getElementById("backdrop-root"))}
		
		{ReactDOM.createPortal(<ModalOverlay onCloseModal={props.onCloseModal} 
		title={props.title} message={props.message}/>,document.getElementById("overlay-root"))}
	</Wrapper>;
};

export default ErrorModal;
