import React, {useState} from "react";
import {useRef} from "react";
import Wrapper from "../Helpers/Wrapper";
import Button from "../UI/Button";
import Card from "../UI/Card";
import ErrorModal from "../UI/ErrorModal";
import styles from "./AddUser.module.css";

const AddUser = (props) => {
	
	const nameInputRef = useRef("");
	const ageImputRef = useRef("");
	const user=useState({username:nameInputRef,age:ageImputRef})
	const [error, setError] = useState();

	const addUserHandler = (event) => {
		event.preventDefault();
		console.log(user)
		let userName = nameInputRef.current.value;
		let userAge = ageImputRef.current.value;

		if (userName.trim().length === 0 || userAge.trim().length === 0) {
			return setError({
				title: "Invalid input",
				message: "Please enter a valid name and age (non-empty values).",
			});
		}

		if (+userAge < 1) {
			return setError({
				title: "Invalid age",
				message: "Please enter a valid age (>0).",
			});
		}
		props.onAddUser({username: userName, age: userAge});
		nameInputRef.current.value = "";
		ageImputRef.current.value = "";
	};

	const errorHandler = () => {
		setError(null);
	};

	return (
		<Wrapper>
			{error && (
				<ErrorModal
					onCloseModal={errorHandler}
					title={error.title}
					message={error.message}
				/>
			)}
			<Card className={styles.input}>
				<form onSubmit={addUserHandler}>
					<label htmlFor="username">Username</label>
					<input id="username" type="text" ref={nameInputRef} />
					<label htmlFor="age">Age (Years)</label>
					<input id="age" type="number" ref={ageImputRef} />
					<Button type="submit">Add User</Button>
				</form>
			</Card>
		</Wrapper>
	);
};

export default AddUser;
