import React, {useState} from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import ErrorModal from "../UI/ErrorModal";
import styles from "./AddUser.module.css";

const AddUser = (props) => {
	const [enteredUser, setEnteredUser] = useState({username: "", age: ""});

	const userChangeHandler = (event) => {
		setEnteredUser((prev) => {
			return {...prev, [event.target.name]: event.target.value};
		});
	};

	const addUserHandler = (event) => {
		event.preventDefault();
		if (
			enteredUser.username.trim().length === 0 ||
			enteredUser.age.trim().length === 0
		) {
			return;
		}

		if (+enteredUser.age < 1) {
			return;
		}
		props.onAddUser(enteredUser);
		setEnteredUser({username: "", age: ""});
	};

	return (
		<div>
			<ErrorModal title="An error accured" message="Something went wrong." />
			<Card className={styles.input}>
				<form onSubmit={addUserHandler}>
					<label htmlFor="username">Username</label>
					<input
						onChange={userChangeHandler}
						value={enteredUser.username}
						name="username"
						id="username"
						type="text"
					/>
					<label htmlFor="age">Age (Years)</label>
					<input
						onChange={userChangeHandler}
						value={enteredUser.age}
						name="age"
						id="age"
						type="number"
					/>
					<Button type="submit">Add User</Button>
				</form>
			</Card>
		</div>
	);
};

export default AddUser;
